mapper.select:
select a.id,ind_id,ind_date,ind_type,ind_info,hou_id,hou_money, ind_money,ind_discount,cus_id,a.co_id,ind_day from indent a inner join house b on a.hou_id and b.hou_id inner join employee c b.emp_id = c.emp_id and a.co_id = c.co_id
c.emp_id = #{emp_id} and c.co_id = #{co_id}
and ind_id = #{indId}

select

from indent

	co_id =#{co_id}
	
		and ind_id = #{indId}

select count(1) from indent a

	inner join company b on a.co_id = b.co_id
	
		inner join employee c on b.co_id = c.co_id
	


	and a.ind_type = '审核中'
	
		and c.emp_id = #{empInfo}
	
	
		and b.co_id=#{empInfo}
