mapper.select:
select a.* from(select rownum as rn, a.id,ind_id,ind_date,ind_type,ind_info,b.hou_id,b.hou_money, ind_money,ind_discount,cus_id,a.co_id,ind_day from indent a inner join house b on a.hou_id = b.hou_id inner join employee c on b.emp_id = c.emp_id and a.co_id = c.co_id
rownum <=(10*#{pageNum}) and c.emp_id = #{empId} and c.co_id = #{coId}
and ind_id = #{indId}
) a where a.rn >= (10*(#{pageNum}-1))

select a.* from(select rownum as rn,

from indent

	rownum <=(10*#{pageNum})
	and co_id =#{coId}
	
		and ind_id = #{indId}
	
)
a where a.rn >= (10*(#{pageNum}-1))

select

from
indent where ind_id = #{indId} and co_id = #{coId}

select count(1) from indent a
inner join company b on a.co_id = b.co_id

	inner join employee c on b.co_id = c.co_id


	and a.ind_type = '审核中'
	
		and c.emp_id = #{empInfo}
	
	
		and b.co_id=#{empInfo}
